/* harmony export */ UpdateManager: () => (/* binding */ UpdateManager)
/* harmony export */ });
class UpdateManager {

	constructor() {
		this.updateMethods = [{}, {}];
		this.currentFocus;
	}

	registerMethod(offset, method) {
		this.updateMethods[offset] = method;
	}

	focusHasChanged(offset) {
		this.currentFocus = offset;
	}

	apply() {
		if (typeof this.currentFocus === 'undefined') {
			return;
		}
		this.updateMethods[this.currentFocus]();
	}
}


//# sourceURL=webpack://radare2-webui-material/./js/helpers/UpdateManager.js?")},"./js/layout/ContainerZone.js":(__unused_webpack_module,__webpack_exports__,__webpack_require__)=>{eval("__webpack_require__.r(__webpack_exports__);
