/** How many screen we want to retrieve in one round-trip with r2 */
const defaultHeightProvisioning = 3;

class RadareInfiniteBlock {

	constructor(heightProvisioning = defaultHeightProvisioning) {
		this.heightProvisioning = heightProvisioning
	}

	/**
	 * Helper to delay drawing
	 */
	getCurChunk() {
		return this.curChunk;
	}

	/**
	 * Helper for dynamic callback at first drawing
	 * Allows to place the scroll on current chunk.
	 */
	getFirstElement() {
		return this.firstElement;
	}

	/**
	 * Load the *new* initial offset from the "s" value
	 */
	refreshInitialOffset() {
		r2.cmd('s', (offset) => {
			this.initialOffset = parseInt(offset, 16);
		});
	}

	/**
	 * Gather data and set event to configure infinite scrolling
	 */
	defineInfiniteParams(trigger) {
		var height = (this.container.getBody().offsetHeight === 0) ? 800 : this.container.getBody().offsetHeight;
		this.howManyLines = Math.floor(height / this.lineHeight * this.heightProvisioning);

		var infiniteScrolling = new _helpers_InfiniteScrolling__WEBPACK_IMPORTED_MODULE_1__.InfiniteScrolling(
			this.container.getBody(),
			3, /* before, current, after */
			(typeof trigger !== 'undefined') ? trigger : 0.20 /* when there less than 1/5 visible */
		);

		infiniteScrolling.setTopEvent((pos, endCallback) => {
			this.nav.go(_core_NavigatorDirection__WEBPACK_IMPORTED_MODULE_0__.NavigatorDirection.BEFORE);
			this.infiniteDrawingContent(_core_NavigatorDirection__WEBPACK_IMPORTED_MODULE_0__.NavigatorDirection.BEFORE, pos, endCallback);
		});

		infiniteScrolling.setBottomEvent((pos, endCallback) => {
			this.nav.go(_core_NavigatorDirection__WEBPACK_IMPORTED_MODULE_0__.NavigatorDirection.AFTER);
			this.infiniteDrawingContent(_core_NavigatorDirection__WEBPACK_IMPORTED_MODULE_0__.NavigatorDirection.AFTER, pos, endCallback);
		});
	}

}


//# sourceURL=webpack://radare2-webui-material/./js/layout/RadareInfiniteBlock.js?')},"./js/layout/Ruler.js":(__unused_webpack_module,__webpack_exports__,__webpack_require__)=>{eval("__webpack_require__.r(__webpack_exports__);
/* harmony export */ __webpack_require__.d(__webpack_exports__, {
/* harmony export */ Ruler: () => (/* binding */ Ruler)
/* harmony export */ });
/** Ruler component for splitted layout */
class Ruler {

	get position() { return this._position; }
	set position(value) {
		this._position = value;
		this.triggerListeners();
	}

	constructor(containerNode, rulerNode) {
		this.containerNode = containerNode;
		this.rulerNode = rulerNode;

		this.listeners = [];
		this.moving = false;
		this.position = 0.5;

		this.init();
		this.reset();

		this.addListeners((position) => this.move(position));
	}

	/** Add events listeners on the node */
	init() {
		const doDrag = (e) => {
			e.preventDefault();
			const containerBoundingBox = this.containerNode.getBoundingClientRect();
			this.position = (e.clientX - containerBoundingBox.left) / containerBoundingBox.width;
		};

		const stopDrag = () => {
			document.documentElement.removeEventListener('mousemove', doDrag, false);
			document.documentElement.removeEventListener('mouseup', stopDrag, false);
		};

		this.rulerNode.addEventListener('mousedown', (e) => {
			document.documentElement.addEventListener('mousemove', doDrag, false);
			document.documentElement.addEventListener('mouseup', stopDrag, false);
		});
	}

	/** Invoke listener with new position */
	triggerListeners() {
		this.listeners.forEach(l => l(this.position));
	}

	addListeners(fn) {
		this.listeners.push(fn);
	}

	/** Move the ruler between [0;1] */
	move(position) {
		this.rulerNode.style.marginLeft = (position) * 100 + '%';
	}

	/** Place the ruler in the middle (doesn't change display mode) */
	reset() {
		this.position = 0.5;
		this.move(0.5);
	}

	show() {
		this.rulerNode.style.display = 'block';
	}

	hide() {
		this.rulerNode.style.display = 'none';
	}
}


//# sourceURL=webpack://radare2-webui-material/./js/layout/Ruler.js?")},"./js/modules/disasm/Disassembly.js":(__unused_webpack_module,__webpack_exports__,__webpack_require__)=>{eval("__webpack_require__.r(__webpack_exports__);
