/* harmony export */ HexPairNavigator: () => (/* binding */ HexPairNavigator)
/* harmony export */ });
/* harmony import */ var _core_BlockNavigator__WEBPACK_IMPORTED_MODULE_0__ = __webpack_require__(/*! ../../core/BlockNavigator */ "./js/core/BlockNavigator.js");
