//# sourceURL=webpack://radare2-webui-material/./js/modules/overview/Overview.js?")},"./js/widgets/BasePreWidget.js":(__unused_webpack_module,__webpack_exports__,__webpack_require__)=>{eval("__webpack_require__.r(__webpack_exports__);
