//# sourceURL=webpack://radare2-webui-material/./js/core/R2Wrapper.js?")},"./js/core/SettingsManager.js":(__unused_webpack_module,__webpack_exports__,__webpack_require__)=>{eval("__webpack_require__.r(__webpack_exports__);
/* harmony export */ __webpack_require__.d(__webpack_exports__, {
/* harmony export */ SettingsManager: () => (/* binding */ SettingsManager)
/* harmony export */ });
class SettingsManager {

	get keys() { return this.itemKeys };

	constructor(keys, baseConf) {
		this.itemKeys = keys;
		this.conf = baseConf;
	}

	loadAll(force = false) {
		for (let key in this.conf) {
			const curValue = this.getItem(key);
			const defaultValue = this.getItemDefaultValue(key);
			if ((!force && curValue !== defaultValue) || force) {
				this.conf[key].apply(curValue);
			}
		}
	}

	resetAll() {
		for (let key in this.conf)
			localStorage.removeItem(key);
		this.loadAll(true);
	}

	getItem(key) {
		if (!this.keyExists(key)) throw new Error(`ConfKey ${key} doesn't exist!`);

		var local = localStorage.getItem(key);
		if (local !== null) {
			if (local === 'false') {
				local = false;
			} else if (local === 'true') {
				local = true;
			}
			return local;
		} else {
			return this.getItemDefaultValue(key);
		}
	}

	setItem(key, value) {
		if (!this.keyExists(key)) throw new Error(`ConfKey ${key} doesn't exist!`);

		localStorage.setItem(key, value);
		this.conf[key].apply(value);
	}

	getItemDefaultValue(key) {
		if (!this.keyExists(key)) throw new Error(`ConfKey ${key} doesn't exist!`);

		return this.conf[key].defVal;
	}

	/** Tell if the key is defined in the declared item keys */
	keyExists(key) {
		return (typeof this.conf[key]) !== 'undefined';
	}
}


//# sourceURL=webpack://radare2-webui-material/./js/core/SettingsManager.js?")},"./js/core/UIContext.js":(__unused_webpack_module,__webpack_exports__,__webpack_require__)=>{eval("__webpack_require__.r(__webpack_exports__);
/* harmony export */ __webpack_require__.d(__webpack_exports__, {
/* harmony export */ uiContext: () => (/* binding */ uiContext)
/* harmony export */ });
/* harmony import */ var _layout_Layouts__WEBPACK_IMPORTED_MODULE_0__ = __webpack_require__(/*! ../layout/Layouts */ "./js/layout/Layouts.js");
/* harmony import */ var _layout_Ruler__WEBPACK_IMPORTED_MODULE_1__ = __webpack_require__(/*! ../layout/Ruler */ "./js/layout/Ruler.js");



// Determine maximum widget displayable at same time
const MAX_WIDGETS = 2;

// Define margin to apply both side of the ruler
const RULER_GAP = 0.001;

/** Define the global UI context to switch between widgets */
class UIContext {

	/**	Boolean to check focus on first widget */
	get isFirstWidgetFocused() { return this.focusedWidget === this.displayedWidgets[0]; }

	/** Tells if current layout is splitted */
	get isSplitted() { return this.currentLayout !== _layout_Layouts__WEBPACK_IMPORTED_MODULE_0__.Layouts.FULL; }

	/** Should be only one UIContext */
	constructor() {
		this.initialized = false;
	}

	/** Bind current widget factory and UI main nodes */
	init(widgetFactory, contentNodeId, rulerNodeId, titleNodeId) {
		if (this.initialized) {
			console.error('UIContext shouldn\'t be initialized more than once.');
		}

		// References to factory and DOM
		this.initialized = true;
		this.widgetFactory = widgetFactory;
		this.contentNode = document.getElementById(contentNodeId);
		this.rulerNode = document.getElementById(rulerNodeId);
		this.titleNode = document.getElementById(titleNodeId);

		// Allocating content node with focus listeners
		for (let i = 0 ; i < MAX_WIDGETS ; i++) {
			const node = document.createElement('div');
			node.addEventListener('click', () => this.setFocusAt(i));
			this.contentNode.appendChild(node);
		}

		// Building the ruler
		this.ruler = new _layout_Ruler__WEBPACK_IMPORTED_MODULE_1__.Ruler(this.contentNode, this.rulerNode);
		this.ruler.hide();
		this.currentLayout = _layout_Layouts__WEBPACK_IMPORTED_MODULE_0__.Layouts.FULL;

		// Listening to ruler moves
		this.ruler.addListeners((position) => {
			this.resizeTo(position);
		});

		// Tracking widget state: focus and display (following only from Widgets, no reference)
		this.displayedWidgets = []; // order is important
		this.focusedWidget;
	}

	/**
	 * Set focus on current widget
	 * @param {any} offset 
	 */
	setFocusAt(offset) {
		if (~this.displayedWidgets.indexOf(offset)) {
			console.error('UIContext: focus offset isn\'t correct');
			return;
		}
		if (this.displayedWidgets[offset] === this.focusedWidget) {
			return;
		}

		const losingFocus = this.focusedWidget;
		const gainingFocus = this.displayedWidgets[offset];

		this.widgetFactory.get(losingFocus).lostFocus();
		this.widgetFactory.get(gainingFocus).gotFocus();

		this.focusedWidget = this.displayedWidgets[offset];

		this.drawTitle();
	}

	/**
	 * Navigate to specified widget by replacing currently focused widget
	 * @param {Widgets} widget Widget reference
	 * @param {...*} args
	 */
	navigateTo(widget, ...args) {
		const destinationNode = this.getSlot(widget);
		this.widgetFactory.get(widget).drawWidget(destinationNode, ...args);
		this.applyLayout(destinationNode);
		this.focusedWidget = widget;
		this.drawTitle();
	}

	/** Provide a slot (node) for specified widget */
	getSlot(widget) {
		let widgetIndex = this.displayedWidgets.indexOf(widget);
		if (~widgetIndex) { // We want a slot for an already displayed widget
			return this.contentNode.children[widgetIndex];
		}

		let replacedWidget;
		if (this.currentLayout === _layout_Layouts__WEBPACK_IMPORTED_MODULE_0__.Layouts.FULL) {
			// There is already a widget
			if (typeof this.displayedWidgets.length !== 0) {
				replacedWidget = this.displayedWidgets[0];
			}
			this.displayedWidgets = [widget];
			widgetIndex = 0;
		} else if (this.displayedWidgets.length === 1) {
			// Just merged, second part is free
			this.displayedWidgets.push(widget);
			widgetIndex = 1;
		} else {
			if (this.isFirstWidgetFocused) {
				replacedWidget = this.displayedWidgets.shift();
				this.displayedWidgets.unshift(widget);
				widgetIndex = 0;
			} else {
				replacedWidget = this.displayedWidgets.pop();
				this.displayedWidgets.push(widget);
				widgetIndex = 1;
			}
		}

		// Notify previously displayed widget that it has been unplugged
		if (typeof replacedWidget !== 'undefined') {
			this.widgetFactory.get(replacedWidget).lostDisplay();
		}

		this.setFocusAt(widgetIndex);
		return this.contentNode.children[widgetIndex];
	}

	/** Draw title with displayed instances */
	drawTitle(separator = ' & ') {
		this.titleNode.innerHTML = '';
		for (let i = 0 ; i < this.displayedWidgets.length ; i++) {
			const widget = this.displayedWidgets[i];
			const isActiveOne = widget === this.focusedWidget && this.displayedWidgets.length > 1;
			const node = document.createElement(isActiveOne ? 'strong' : 'span');
			node.textContent = this.widgetFactory.get(widget).name;
			node.addEventListener('click', () => this.setFocusAt(i));

			if (i !== 0) {
				this.titleNode.appendChild(
					document.createTextNode(separator)
				);
			}

			this.titleNode.appendChild(node);
		}
	}

	/**
	 * Apply classes to render current layout
	 * @param {any} node 
	 */
	applyLayout(node) {
		if (this.currentLayout === _layout_Layouts__WEBPACK_IMPORTED_MODULE_0__.Layouts.VERTICAL) {
			node.classList.add('vertical');
		}
	}

	/** Resize a splitted layout between [0;1]% */
	resizeTo(position) {
		if (!this.isSplitted) {
			return;
		}

		this.contentNode.children[0].style.width = (position - RULER_GAP) * 100 + '%';
		this.contentNode.children[1].style.width = (1 - position - RULER_GAP) * 100 + '%';
	}

	/**
	 * Split current layout
	 * @param {Layouts} layout Layout to use
	 */
	split(layout) {
		if (this.isSplitted) {
			return;
		}

		if (layout !== _layout_Layouts__WEBPACK_IMPORTED_MODULE_0__.Layouts.VERTICAL) {
			console.error('Not supported layout.');
		}

		this.currentLayout = layout;
		this.ruler.show();

		for (const child of this.contentNode.children) {
			this.applyLayout(child);
		}
	}

	/** Merge current layout, we keep currently focused */
	merge() {
		if (!this.isSplitted) {
			return;
		}

		this.ruler.reset();
		this.ruler.hide();
		this.currentLayout = _layout_Layouts__WEBPACK_IMPORTED_MODULE_0__.Layouts.FULL;

		const focusedNode = this.isFirstWidgetFocused ? this.contentNode.children[0] : this.contentNode.children[1];
		const otherNode = this.isFirstWidgetFocused ? this.contentNode.children[1] : this.contentNode.children[0];

		// We updates styles accordingly (we don't rewrite className for the focused one to keep eventual classes)
		otherNode.className = 'rwidget';
		focusedNode.classList.remove('vertical');
		focusedNode.classList.remove('horizontal');
		focusedNode.classList.add('full');

		// We swap first and second child if second one was the focused one
		if (focusedNode !== this.contentNode.children[0]) {
			focusedNode.parentNode.insertBefore(otherNode, focusedNode);
		}
	}
}

const uiContext = new UIContext();


//# sourceURL=webpack://radare2-webui-material/./js/core/UIContext.js?")},"./js/helpers/Autocompletion.js":(__unused_webpack_module,__webpack_exports__,__webpack_require__)=>{eval("__webpack_require__.r(__webpack_exports__);
/* harmony export */ __webpack_require__.d(__webpack_exports__, {
/* harmony export */ Autocompletion: () => (/* binding */ Autocompletion)
/* harmony export */ });
/* harmony import */ var _core_R2Wrapper__WEBPACK_IMPORTED_MODULE_0__ = __webpack_require__(/*! ../core/R2Wrapper */ "./js/core/R2Wrapper.js");
/* harmony import */ var _widgets_Widgets__WEBPACK_IMPORTED_MODULE_1__ = __webpack_require__(/*! ../widgets/Widgets */ "./js/widgets/Widgets.js");



const ACKeys = {
	UP: 38,
	DOWN: 40,
	ENTER: 13
}

const ACNodes = {
	EMPTY: {pos: -1, offset: 0, length: 0, name: 'No match!'}
}

/**
 * Autocompletion classe, define a way to build an autocompletion process
 * with a fixed set of entries.
 */
class Autocompletion {

	/**
	 * @param {String} formId - Literal DOM id #field
	 * @param {String} choicesId - Literal DOM id #dropdown
	 * @param {String} cmd - run into r2 to populate the autocompletion, eg. 'fs *;fj'
	 * @param {integer} minChar - number of charcaters to start autocompletion
	 * @param {integer} maxProp - maximum propositions to offer
	 */
	constructor(formId, choicesId, cmd, minChar, maxProp) {
		this.form_ = formId;
		this.dropdown_ = choicesId;
		this.cmd_ = cmd;
		this.minChar_ = minChar || 2;
		this.maxProp_ = maxProp || 10;
		this.init_();
	}

	init_() {
		this.form_ = document.getElementById(this.form_);
		this.dropdown_ = document.getElementById(this.dropdown_);

		var boundKeyUpHandler = this.keyHandler.bind(this);
		this.form_.addEventListener('keyup', boundKeyUpHandler);

		var _this = this;
		this.form_.addEventListener('focus', function() {
			if (_this.prevLength_ >= _this.minChar_) {
				_this.show();
			}
		});

		this.form_.addEventListener('blur', function() {
			_this.hide();
		});

		this.flags_;
		this.activeChoice_ = 0;
		this.prevLength_ = 0;
		this.list_;
		this.completions_;

		this.populate_();
	}

	populate_() {
		var _this = this;
		r2.cmdj(this.cmd_, function(f) {
			_this.flags_ = f;
		});
	}

	process_(str) {
		var selectedFlags = [];

		var howMany = 0;
		for (var i = 0 ; i < this.flags_.length ; i++) {
			var offset = this.flags_[i].name.indexOf(str, 0);
			if (offset !== -1) {
				selectedFlags.push({
					pos: howMany++,
					offset: offset,
					length: str.length,
					name: this.flags_[i].name
				});
			}

			if (howMany === this.maxProp_) {
				return selectedFlags;
			}
		}
		return selectedFlags;
	}

	addNode_(item, active) {
		const node = document.createElement('li');
		if (active) {
			node.className = 'active';
		}

		node.addEventListener('mouseover', () => this.setActiveChoice(item.pos));

		node.addEventListener('mousedown', () => {
			this.setActiveChoice(item.pos);
			this.valid();
		});

		const emphasis = document.createElement('strong');
		emphasis.appendChild(document.createTextNode(item.name.substr(item.offset, item.length)));

		node.appendChild(
			document.createTextNode(
				item.name.substr(0, item.offset)));
		node.appendChild(emphasis);
		node.appendChild(
			document.createTextNode(
				item.name.substr(item.offset + item.length, item.name.length - (item.offset + item.length))));
		this.dropdown_.appendChild(node);
		console.log(node);
	}

	cleanChoices_() {
		// Cleaning old completion
		while (this.dropdown_.firstChild) {
			this.dropdown_.removeChild(this.dropdown_.firstChild);
		}
	}

	setActiveChoice(newActive) {
		let i = 0;
		for (let child of this.dropdown_.children) {
			child.className = i === newActive ? 'active' : '';
			i += 1;
		}
		this.activeChoice_ = newActive;
	}

	keyMovement_(key) {
		if (key === ACKeys.UP && this.activeChoice_ > 0) {
			console.log('UP');
			this.setActiveChoice(this.activeChoice_ - 1);
		}

		if (key === ACKeys.DOWN && this.activeChoice_ < this.dropdown_.childNodes.length - 1) {
			console.log('DOWN');
			this.setActiveChoice(this.activeChoice_ + 1);
		}
	}

	valid() {
		if (this.activeChoice_ === -1 || this.dropdown_.childNodes.length <= this.activeChoice_) {
			return;
		}
		this.hide();
		this.prepareView();
		return _core_R2Wrapper__WEBPACK_IMPORTED_MODULE_0__.r2Wrapper.seek(this.completions_[this.activeChoice_].name, _widgets_Widgets__WEBPACK_IMPORTED_MODULE_1__.Widgets.DISASSEMBLY);
	}

	show() {
		this.dropdown_.style.display = 'block';
	}

	hide() {
		this.dropdown_.style.display = 'none';
	}

	keyHandler(e) {
		if (e.keyCode === ACKeys.UP || e.keyCode === ACKeys.DOWN) {
			return this.keyMovement_(e.keyCode);
		}

		if (e.keyCode === ACKeys.ENTER) {
			this.hide();
			return this.valid();
		}

		var value = e.target.value;
		this.cleanChoices_();

		if (value.length >= 2) {
			this.show();
			this.completions_ = this.process_(value);
			if (this.prevLength_ !== value.length) {
				this.activeChoice_ = 0;
			}

			// Add them to dropdown
			if (this.completions_.length === 0) {
				this.addNode_(ACNodes.EMPTY, false);
			} else {
				for (var i in this.completions_) {
					// TODO add eventthis.list_ener (hover) for this.activeChoice_
					this.addNode_(this.completions_[i], i === this.activeChoice_);
				}
			}

			this.prevLength_ = value.length;
		} else {
			this.hide();
		}
	}

	setPrepareView(callback) {
		this.preparationCallback = callback;
	}

	/**
	 * Prepare view to show the result
	 */
	prepareView() {
		if (typeof this.preparationCallback === 'undefined') {
			return;
		}
		this.preparationCallback();
	}
}


//# sourceURL=webpack://radare2-webui-material/./js/helpers/Autocompletion.js?")},"./js/helpers/Format.js":(__unused_webpack_module,__webpack_exports__,__webpack_require__)=>{eval("__webpack_require__.r(__webpack_exports__);
