		this.updateManagers.updates = new _helpers_UpdateManager__WEBPACK_IMPORTED_MODULE_0__.UpdateManager();
		this.updateManagers.lastViews = new _helpers_UpdateManager__WEBPACK_IMPORTED_MODULE_0__.UpdateManager();

		this.addFocusListener(this.updateManagers.updates);
		this.addFocusListener(this.updateManagers.lastViews);
