/**
 * Very simple Widget designed to handle RAW content from R2
 * Would be eventually formatted then encapsulated with <pre> 
 */
class BasePreWidget extends _BaseWidget__WEBPACK_IMPORTED_MODULE_0__.BaseWidget {

	/**
	 * Creates an instance of BasePreWidget.
	 * @param {any} name Name of the widget, displayed on the top bar
	 * @param {any} formatFunc Function used to format the r2cmd output (used as innerHTML)
	 * @param {any} r2cmd Command executed to obtain the output
	 * @param {any} [backButton=null] Optional button to be inserted into the widget
	 */
	constructor(name, formatFunc, r2cmd, backButton = null) {
		super(name, 'dark');
		this.formatFunc = formatFunc;
		this.r2cmd = r2cmd;
		if (backButton !== null)
			this.backButton = backButton;
	}

	/** @override */
	init() {
		if (typeof this.backButton === 'undefined') {
			return;
		}
		this.backButton.style.position = 'absolute';
		this.backButton.style.top = '1em';
		this.backButton.style.left = '1em';

		_core_R2Wrapper__WEBPACK_IMPORTED_MODULE_2__.r2Wrapper.registerListener(_core_R2Wrapper__WEBPACK_IMPORTED_MODULE_2__.R2Actions.SEEK, () => {
			if (this.displayed) {
				this.draw();
			}
		});
	}

	/** @override */
	draw() {
		if (typeof this.backButton !== 'undefined') {
			this.node.appendChild(this.backButton);
		}
		this.node.appendChild(this.getPre());
	}

	/** Format text from registered r2cmd and provides a <pre> element */
	getPre() {
		const pre = document.createElement('pre');
		r2.cmd(this.r2cmd, output => {
			pre.appendChild(this.formatFunc(output));
		});
		return pre;
	}
}


//# sourceURL=webpack://radare2-webui-material/./js/widgets/BasePreWidget.js?")},"./js/widgets/BaseWidget.js":(__unused_webpack_module,__webpack_exports__,__webpack_require__)=>{eval("__webpack_require__.r(__webpack_exports__);
/* harmony export */ __webpack_require__.d(__webpack_exports__, {
/* harmony export */ BaseWidget: () => (/* binding */ BaseWidget)
/* harmony export */ });
/**
 * BaseWidget is an abstract class which wrap a Widget
 * This abstraction ensure two concerns:
 * - A Widget can be instanciated several times if we want
 * - A Widget shouldn't be bothered by dimension change (TODO)
 */
class BaseWidget {

	/** Node provided by UIContext to draw content */
	get rootNode() { return this._rootNode; }

	/** Node provided by the Widget to draw content */
	get node() { return this._node; }

	/** Pass the widgetContainer instance with name of the widget */
	constructor(name, ...classNames) {
		this.name = name;
		this.classNames = classNames;
		this.classNames.push('rwidget');
		this.init();

		this.focused = false;
		this.displayed = false;
	}

	/** Init the module used inside component, called once */
	init() { }

	/** Define what should be done to render the Widget */
	drawWidget(destinationNode, ...args) {
		this._node = destinationNode;
		this._rootNode = destinationNode;
		this._rootNode.focus();

		// Set state
		this.displayed = true;
		this.focused = true;

		// Clear previous content
		this._node.innerHTML = '';
		this._node.className = '';

		// Apply CSS classes
		this.classNames.forEach(className => this._rootNode.classList.add(className));

		// Insert content
		this.draw(...args);
	}

	/** Method to insert content to Widget.node */
	draw(...args) { }

	/** When focus is gained */
	gotFocus() {
		this.gotDisplay();
		this.focused = true;
	}

	/** When focus is lost */
	lostFocus() {
		this.focused = false;
	}

	/** When widget is displayed */
	gotDisplay() {
		this.displayed = true;
	}

	/** When widget is replaced */
	lostDisplay() {
		this.lostFocus();
		this.displayed = false;
	}
}


