class FlagsSpacesWidget extends _BaseWidget__WEBPACK_IMPORTED_MODULE_0__.BaseWidget {
	activeEls = [];

	constructor() {
		super('Flag Spaces');
	}

	init() {
		_core_R2Wrapper__WEBPACK_IMPORTED_MODULE_2__.r2Wrapper.registerListener(_core_R2Wrapper__WEBPACK_IMPORTED_MODULE_2__.R2Actions.SEEK, () => {
			if (!this.displayed) {
				return;
			}
			this.draw();
		});
	}

	draw() {
		this.node.innerHTML = '';
		this.node.scrollTop = 0;
		this.current = null;
		this.node.appendChild(this.getPanel());
	}

	getPanel() {
		var c = document.createElement('div');
		if (this.inColor) {
			c.style.backgroundColor = '#202020';
		}

		var header = document.createElement('div');
		header.style.position = 'fixed';
		header.style.margin = '0.5em';
		c.appendChild(header);

		header.appendChild(_helpers_Inputs__WEBPACK_IMPORTED_MODULE_5__.Inputs.iconButton('undo', 'Back to flags', () => _core_UIContext__WEBPACK_IMPORTED_MODULE_1__.uiContext.navigateTo(_Widgets__WEBPACK_IMPORTED_MODULE_3__.Widgets.FLAGS)));
		header.appendChild(_helpers_Inputs__WEBPACK_IMPORTED_MODULE_5__.Inputs.button('Deselect', () => { this.current = null; r2.cmd('fs *', () => this.draw()); } ));
		header.appendChild(_helpers_Inputs__WEBPACK_IMPORTED_MODULE_5__.Inputs.button('Add', () => this.setFlagspace()));
		header.appendChild(_helpers_Inputs__WEBPACK_IMPORTED_MODULE_5__.Inputs.button('Delete', () => this.delFlagspace()));
		header.appendChild(_helpers_Inputs__WEBPACK_IMPORTED_MODULE_5__.Inputs.button('Rename', () => this.renameFlagspace()));

		var content = document.createElement('div');
		content.appendChild(document.createTextNode('Click on a row to select it.'));
		content.style.paddingTop = '70px';
		c.appendChild(content);

		r2.cmd('fsj', (d) => {
			const data = JSON.parse(d);
			const table = new _helpers_Table__WEBPACK_IMPORTED_MODULE_4__.Table(
				['+Flags', 'Flagspace'],
				[true, false],
				'flagspaceTable');

			this.activeEls = this.activeEls.filter(e => {
				e.classList.remove(['active']);
				return false;
			});

			data.map( x => {
				const a = document.createElement('a');
				a.textContent = x.name;
				if (x.selected){
					a.classList.add(['active']);
					this.activeEls.push(a);
				}

				a.addEventListener('click', (e) => {
					console.log(x.name, e);
					r2.cmd('fs ' + x.name, (h)=>{
						this.activeEls = this.activeEls.filter(e => {
							e.classList.remove(['active']);
							return false;
						});
						a.classList.add(['active']);
						this.activeEls.push(a);
					});
					this.draw();
				});

				const row = table.addRow([x.count, a]);
				row.addEventListener('click', () => {
					table.getRows().forEach((curRow) => {
						curRow.classList.remove('active');
					});
					row.classList.add('active');
					this.current = x.name;
				});
			});
			table.insertInto(content);
		});

		return c;
	}

	setFlagspace() {
		let fs = this.current;
		if (!fs) {
			fs = prompt('name');
		}
		if (!fs) {
			return;
		}
		r2.cmd('fs ' + fs, () => {
			this.draw();
		});
	}

	renameFlagspace() {
		let fs = this.current;
		if (!fs) {
			fs = prompt('name');
		}
		if (!fs) {
			return;
		}
		r2.cmd('fsr ' + fs, () => {
			this.draw();
		});
	}


	delFlagspace() {
		let fs = this.current;
		if (!fs) {
			fs = '.';
		}
		if (!fs) {
			return;
		}
		r2.cmd('fs-' + fs, () => {
			this.draw();
		});
	}
}


