/* harmony import */ var _helpers_Inputs__WEBPACK_IMPORTED_MODULE_3__ = __webpack_require__(/*! ../helpers/Inputs */ "./js/helpers/Inputs.js");
/* harmony import */ var _core_R2Wrapper__WEBPACK_IMPORTED_MODULE_4__ = __webpack_require__(/*! ../core/R2Wrapper */ "./js/core/R2Wrapper.js");







class ScriptWidget extends _BaseWidget__WEBPACK_IMPORTED_MODULE_0__.BaseWidget {
	constructor() {
		super('Script');
	}

	init() {
		_core_R2Wrapper__WEBPACK_IMPORTED_MODULE_4__.r2Wrapper.registerListener(_core_R2Wrapper__WEBPACK_IMPORTED_MODULE_4__.R2Actions.SEEK, () => {
			if (this.displayed) {
				this.draw();
			}
		});
	}

	draw() {
		this.toggleFoo = '';
		this.node.appendChild(this.getPanel());
	}

	getPanel() {
		var c = document.createElement('div');

		c.appendChild(_helpers_Inputs__WEBPACK_IMPORTED_MODULE_3__.Inputs.button('Run', () => this.runScript()));
		c.appendChild(_helpers_Inputs__WEBPACK_IMPORTED_MODULE_3__.Inputs.button('Indent', () => this.indentScript()));
		c.appendChild(_helpers_Inputs__WEBPACK_IMPORTED_MODULE_3__.Inputs.button('Output', () => this.toggleScriptOutput()));
		// c.appendChild(Inputs.button('Console', () => uiContext.navigateTo(Widgets.CONSOLE)));

		c.appendChild(document.createElement('br'));

		const textarea = document.createElement('textarea');
		textarea.id = 'script';
		textarea.rows = 20;
		textarea.className = 'pre';
		textarea.style.width = '100%';
		c.appendChild(textarea);

		c.appendChild(document.createElement('br'));

		const output = document.createElement('div');
		output.id = 'scriptOutput';
		output.className = 'output';
		c.appendChild(output);

		var localScript = localStorage.getItem('script');
		if (!localScript) {
			localScript = 'r2.cmd("?V", log);';
		}
		textarea.value = localScript;

		return c;
	}

	toggleScriptOutput() {
		var o = document.getElementById('scriptOutput');
		if (o) {
			if (this.toggleFoo === '') {
				this.toggleFoo = o.innerHTML;
				o.innerHTML = '';
			} else {
				o.innerHTML = this.toggleFoo;
				this.toggleFoo = '';
			}
		}
	}

	indentScript() {
		var str = document.getElementById('script').value;
		var indented = /* NOT DEFINED js_beautify*/ (str);
		document.getElementById('script').value = indented;
		localStorage.script = indented;
	}

	runScript() {
		var str = document.getElementById('script').value;
		localStorage.script = str;
		document.getElementById('scriptOutput').innerHTML = '';
		try {
			var msg = '"use strict";' +
			'function log(x) { var a = ' +
			'document.getElementById(\'scriptOutput\'); ' +
			'if (a) a.innerHTML += x + \'\\n\'; }\n';
			// CSP violation here
			eval(msg + str);
		} catch (e) {
			alert(e);
		}
	}
}


//# sourceURL=webpack://radare2-webui-material/./js/widgets/ScriptWidget.js?")},"./js/widgets/SearchWidget.js":(__unused_webpack_module,__webpack_exports__,__webpack_require__)=>{eval("__webpack_require__.r(__webpack_exports__);
/* harmony export */ __webpack_require__.d(__webpack_exports__, {
/* harmony export */ SearchWidget: () => (/* binding */ SearchWidget)
/* harmony export */ });
/* harmony import */ var _BaseWidget__WEBPACK_IMPORTED_MODULE_0__ = __webpack_require__(/*! ./BaseWidget */ "./js/widgets/BaseWidget.js");
/* harmony import */ var _helpers_Inputs__WEBPACK_IMPORTED_MODULE_1__ = __webpack_require__(/*! ../helpers/Inputs */ "./js/helpers/Inputs.js");
/* harmony import */ var _helpers_Format__WEBPACK_IMPORTED_MODULE_2__ = __webpack_require__(/*! ../helpers/Format */ "./js/helpers/Format.js");
/* harmony import */ var _core_R2Wrapper__WEBPACK_IMPORTED_MODULE_3__ = __webpack_require__(/*! ../core/R2Wrapper */ "./js/core/R2Wrapper.js");





function searchResults(d) {
	const node = document.getElementById('search_output');
	node.innerHTML = "";
	node.appendChild((0,_helpers_Format__WEBPACK_IMPORTED_MODULE_2__.formatOffsets)(d));
}

class SearchWidget extends _BaseWidget__WEBPACK_IMPORTED_MODULE_0__.BaseWidget {
	constructor() {
		super('Search');
	}

	init() {
		_core_R2Wrapper__WEBPACK_IMPORTED_MODULE_3__.r2Wrapper.registerListener(_core_R2Wrapper__WEBPACK_IMPORTED_MODULE_3__.R2Actions.SEEK, () => {
			if (this.displayed) {
				this.draw();
			}
		});
	}

	draw() {
		this.node.appendChild(this.getPanel());
	}

	getPanel() {
		const c = document.createElement('div');
		var header = document.createElement('div');
		header.style.margin = '0.5em';
		c.appendChild(header);

		const form = document.createElement('input');
		form.id = 'search_input';
		form.className = 'mdl-card--expand mdl-textfield__input';
		form.style.backgroundColor = 'white';
		form.style.paddingLeft = '10px';
		form.style.top = '3.5em';
		form.style.height = '1.8em';
		form.addEventListener('keypress', (e) => this.searchKey(e.keyCode));

		header.appendChild(form);
		header.appendChild(document.createElement('br'));

		header.appendChild(_helpers_Inputs__WEBPACK_IMPORTED_MODULE_1__.Inputs.button('Hex', () => this.runSearch()));
		header.appendChild(_helpers_Inputs__WEBPACK_IMPORTED_MODULE_1__.Inputs.button('String', () => this.runSearchString()));
		header.appendChild(_helpers_Inputs__WEBPACK_IMPORTED_MODULE_1__.Inputs.button('Code', () => this.runSearchCode()));
		header.appendChild(_helpers_Inputs__WEBPACK_IMPORTED_MODULE_1__.Inputs.button('ROP', () => this.runSearchROP()));
		header.appendChild(_helpers_Inputs__WEBPACK_IMPORTED_MODULE_1__.Inputs.button('Magic', () => this.runSearchMagic()));

		const content = document.createElement('div');
		content.id = 'search_output';
		content.style.paddingTop = '50px';
		content.style.color = 'black';
		content.className = 'pre';
		c.appendChild(content);

		return c;
	}

	searchKey(keyCode) {
		var inp = document.getElementById('search_input');
		if (keyCode === 13) {
			this.runSearch(inp.value);
			inp.value = '';
		}
	}

	runSearchMagic() {
		r2.cmd('/m', searchResults);
	}

	runSearchCode(text) {
		if (!text) {
			text = document.getElementById('search_input').value;
		}
		r2.cmd('"/c ' + text + '"', searchResults);
	}

	runSearchString(text) {
		if (!text) {
			text = document.getElementById('search_input').value;
		}
		r2.cmd('/ ' + text, searchResults);
	}

	runSearchROP(text) {
		if (!text) {
			text = document.getElementById('search_input').value;
		}
		r2.cmd('"/R ' + text + '"', searchResults);
	}

	runSearch(text) {
		if (!text) {
			text = document.getElementById('search_input').value;
		}
		if (text.startsWith('"') && text.endsWith('"')) {
			const a = text.replace(/"/g, '');
			r2.cmd('/ ' + a, searchResults);
		} else {
			r2.cmd('"/x ' + text + '"', searchResults);
		}
	}
}


