document.body.onkeypress = function(e) {
	if (e.ctrlKey) {
		const keys = [
			// panelConsole,
			// panelDisasm,
			// panelDebug,
			// panelHexdump,
			// panelFunctions,
			// panelFlags,
			// panelOverview,
			// panelSettings,
			// panelSearch
		];
		if (e.charCode === 'o'.charCodeAt(0)) {
			_core_R2Wrapper__WEBPACK_IMPORTED_MODULE_0__.r2Wrapper.seek();
		}
		let k = e.charCode - 0x30;
		if (k >= 0 && k < keys.length) {
			let fn = keys[k];
			if (fn) {
				fn();
			}
		}
	}
};

/**
 * Add an onClick listener
 * @param {string} nodeId Node concerned
 * @param {function} fn Callback function
 */
function onClick(nodeId, fn) {
	const h = document.getElementById(nodeId);
	if (h) {
		h.addEventListener('click', function() {
			fn();
		});
	} else {
		console.error('onclick-error', nodeId);
	}
}


