function speak(text, callback) {
	if (!_core_R2Wrapper__WEBPACK_IMPORTED_MODULE_0__.r2Settings.getItem(_core_R2Wrapper__WEBPACK_IMPORTED_MODULE_0__.r2Settings.keys.USE_TTS)) {
		return;
	}

	if (typeof SpeechSynthesisUtterance === 'undefined') {
		return;
	}

	var u = new SpeechSynthesisUtterance();
	u.text = text;
	u.lang = 'en-US';

	u.onend = function() {
		if (callback) {
			callback();
		}
	};

	u.onerror = function(e) {
		if (callback) {
			callback(e);
		}
	};

	speechSynthesis.speak(u);
}


//# sourceURL=webpack://radare2-webui-material/./js/helpers/Speak.js?")},"./js/helpers/Table.js":(__unused_webpack_module,__webpack_exports__,__webpack_require__)=>{eval("__webpack_require__.r(__webpack_exports__);
