		table.className = 'disasm-table-dialog';
		table.style.width = '100%';
		table.style.border = '1px dashed red';
		container.appendChild(table);

		var thead = document.createElement('thead');
		table.appendChild(thead);

		var keys = Object.keys(list[0]);
		for (var i = 0 ; i < keys.length ; i++) {
			var th = document.createElement('th');
			th.appendChild(document.createTextNode(keys[i]));
			thead.appendChild(th);
		}

		var tbody = document.createElement('tbody');
		table.appendChild(tbody);

		for (var i = 0 ; i < list.length ; i++) {
			var tr = document.createElement('tr');
			tbody.appendChild(tr);

			for (var j = 0 ; j < keys.length ; j++) {
				var td = document.createElement('td');
				tr.appendChild(td);

				if (keys[j] === 'opcode') {
					td.appendChild((0,_helpers_Format__WEBPACK_IMPORTED_MODULE_6__.formatOffset)(list[i][keys[j]]));
				} else if (keys[j] === 'from') {
					const hex = '0x' + list[i][keys[j]].toString(16);
					td.appendChild((0,_helpers_Format__WEBPACK_IMPORTED_MODULE_6__.formatOffset)(hex));
				} else {
					td.innerHTML = list[i][keys[j]];
				}
			}
		}

		var actions = document.createElement('div');
		actions.className = 'mdl-dialog__actions';
		dialog.appendChild(actions);

		var closeButton = document.createElement('button');
		closeButton.className = 'mdl-button';
		closeButton.innerHTML = 'Close';
		closeButton.addEventListener('click', function() {
			dialog.close();
			document.body.removeChild(dialog);
		});
		actions.appendChild(closeButton);

		document.body.appendChild(dialog);
		componentHandler.upgradeDom();

		dialog.showModal();
	}
