		this.beingSelected = false;
		this.selectionFirst;
		this.selectionEnd;

		this.lastColorUsed = -1;
		this.bgColors = [
			'rgba(255,0,0,0.2)',
			'rgba(0,255,0,0.2)',
			'rgba(0,92,192,0.2)',
			'rgba(255,255,0,0.2)',
			'rgba(255,0,255,0.2)',
			'rgba(0,255,255,0.2)'
		];

