/* harmony export */ "AnalysisCard": () => (/* binding */ AnalysisCard)
/* harmony export */ });
class AnalysisCard {

	get DOM() { return this.card; }
	set onAnalysis(value ) { this.analysisCallback = value; }

	constructor() {
		this.analysisMethods = [
			{ id: 'symbols', name: 'Analyse symbols', cmd: 'aa' },
			{ id: 'ref', name: 'Analyse References', cmd: 'aar' },
			{ id: 'calls', name: 'Analyse calls', cmd: 'e anal.calls=true;aac', disabledCmd: 'e anal.calls=false' },
			{ id: 'emu', name: 'Emulate code', cmd: 'e asm.emu=1;aae;e asm.emu=0', disabledCmd: 'e asm.emu=false' },
			{ id: 'prelude', name: 'Find preludes', cmd: 'aap' },
			{ id: 'autoname', name: 'Autoname fcns', cmd: 'aan' },
		];

		this.build();
	}

	build() {
		this.card = document.createElement('div');
		this.card.className = 'demo-options mdl-card mdl-color--teal-300 mdl-shadow--2dp mdl-cell mdl-cell--4-col mdl-cell--3-col-tablet mdl-cell--12-col-desktop';

		const title = document.createElement('div');
		title.className = 'mdl-card__title mdl-card--expand mdl-color--teal-300';
		title.innerHTML = '<h2 class="mdl-card__title-text">Analysis Options</h2>';
		this.card.appendChild(title);

		const content = document.createElement('div');
		content.className = 'mdl-card__supporting-text mdl-color-grey-600';
		this.card.appendChild(content);

		const choiceList = document.createElement('ul');
		content.appendChild(choiceList);

		const action = document.createElement('div');
		action.className = 'mdl-card__actions mdl-card--border';
		this.card.appendChild(action);

		const analyseButton = document.createElement('a');
		analyseButton.className = 'mdl-button mdl-js-button mdl-js-ripple-effect mdl-color--blue-grey-50 mdl-color-text--blue-greu-50';
		analyseButton.textContent = 'Analyse',
		analyseButton.addEventListener('click', () => this.analyse());
		action.appendChild(analyseButton);

		const spacer = document.createElement('div');
		spacer.className = 'mdl-layout-spacer';
		action.appendChild(spacer);

		const icon = document.createElement('i');
		icon.className = 'material-icons';
		icon.textContent = 'room';
		action.appendChild(icon);

		this.addAnalysisOptions(choiceList);
	}

	addAnalysisOptions(dom) {
		for (let i in this.analysisMethods)
		{
			const method = this.analysisMethods[i];
			const methodId = 'anal_' + method.id;
			const li = document.createElement('li');
			dom.appendChild(li);

			const label = document.createElement('label');
			label.className = 'mdl-checkbox mdl-js-checkbox mdl-js-ripple-effect';
			label.for = methodId;
			li.appendChild(label);

			const checkbox = document.createElement('input');
			checkbox.className = 'mdl-checkbox__input';
			checkbox.id = methodId;
			checkbox.type = 'checkbox';
			label.appendChild(checkbox);

			const text = document.createElement('span');
			text.className = 'mdl-checkbox__label';
			text.innerHTML = method.name;
			label.appendChild(text);
		}
	}

	refresh() {
		var collection = [].slice.call(this.card.getElementsByTagName('input'));
		collection.forEach((checkbox) => checkbox.checked = false);
	}

	analyse() {
		let atLeastOneChecked = false;
		for (let i in this.analysisMethods)
		{
			const method = this.analysisMethods[i];
			const methodId = 'anal_' + method.id;
			const element = document.getElementById(methodId);

			if (element.checked) {
				r2.cmd(method.cmd);
				atLeastOneChecked = true;
			} else if (typeof method.disabledCmd !== 'undefined') {
				r2.cmd(method.cmd);
			}
		}

		if (atLeastOneChecked && typeof this.analysisCallback !== 'undefined') {
			this.analysisCallback();
		}
	}
}


//# sourceURL=webpack://radare2-webui-material/./js/modules/overview/AnalysisCard.js?")},"./js/modules/overview/EntropyCard.js":(__unused_webpack_module,__webpack_exports__,__webpack_require__)=>{eval("__webpack_require__.r(__webpack_exports__);
/* harmony export */ __webpack_require__.d(__webpack_exports__, {
/* harmony export */ "EntropyCard": () => (/* binding */ EntropyCard)
/* harmony export */ });
/* harmony import */ var _core_R2Wrapper__WEBPACK_IMPORTED_MODULE_0__ = __webpack_require__(/*! ../../core/R2Wrapper */ "./js/core/R2Wrapper.js");
/* harmony import */ var _widgets_Widgets__WEBPACK_IMPORTED_MODULE_1__ = __webpack_require__(/*! ../../widgets/Widgets */ "./js/widgets/Widgets.js");



const xmlns = "http://www.w3.org/2000/svg";

class EntropyCard {

	get DOM() { return this.card; }

	constructor(width, height) {
		this.width = width;
		this.height = height;
		this.entropy = [];
		this.build();
		this.refreshEntropy();
		this.draw();
	}

	build() {
		this.card = document.createElement('div');
		this.card.className = 'demo-charts mdl-color--white mdl-shadow--2dp mdl-cell mdl-cell--12-col mdl-grid';
		this.card.style.textAlign = 'center';

		this.svg = document.createElementNS(xmlns, 'svg');
		this.card.appendChild(this.svg);
		this.svg.style.display = 'block';
		this.svg.style.margin = 'auto';
		this.svg.setAttribute('fill', 'currentColor');
		this.svg.setAttribute('viewBox', '0 0 ' + this.width + ' ' + this.height);
		this.svg.setAttribute('width', this.width + 'px');
		this.svg.setAttribute('height', this.height + 'px');
		this.svg.setAttribute('title', 'Entropy graph');
	}

	refreshEntropy() {
		r2.cmdj('p=ej 50 $s @ $M|', (d) => {
			if (d && d.entropy) {
				this.entropy = d.entropy;
			}
		});
	}

	draw() {
		const nbVals = this.entropy.length;
		if (nbVals < 1) {
			return;
		}
		const minVal = this.entropy.reduce((prev, curr) => (prev.value < curr.value) ? prev : curr).value;
		const maxVal = this.entropy.reduce((prev, curr) => (prev.value > curr.value) ? prev : curr).value;
		const width = this.width / nbVals;
		const height = this.height;

		this.svg.innerHTML = '';
		for (let i in this.entropy)
		{
			const cur = this.entropy[i];
			const opacity = 0.1 + (1 - 0.1) * ((cur.value - minVal) / (maxVal - minVal));

			const g = document.createElementNS(xmlns, 'g');
			g.addEventListener('click', () => { _core_R2Wrapper__WEBPACK_IMPORTED_MODULE_0__.r2Wrapper.seek(cur.addr, _widgets_Widgets__WEBPACK_IMPORTED_MODULE_1__.Widgets.DISASSEMBLY); });
			this.svg.appendChild(g);

			const title = document.createElementNS(xmlns, 'title');
			title.textContent = '0x' + cur.addr.toString(16);
			g.appendChild(title);

			const rect = document.createElementNS(xmlns, 'rect');
			rect.setAttribute('x', width * i);
			rect.setAttribute('y', 0);
			rect.setAttribute('width', width);
			rect.setAttribute('height', height);
			rect.setAttribute('fill', '#000');
			rect.setAttribute('fill-opacity', opacity);

			const text = document.createElementNS(xmlns, 'text');
			const color = (opacity > 0.4) ? '#EEEEEE' : 'black';
			text.setAttribute('x', width * i + 2);
			text.setAttribute('y', height + 10);
			text.setAttribute('fill', color);
			text.setAttribute('font-family', 'Roboto');
			text.setAttribute('font-size', 12);
			text.setAttribute('transform', 'rotate(-90, ' + width * i + ', ' + height + ')');
			text.textContent = '0x' + cur.addr.toString(16);

			g.appendChild(rect); 
			g.appendChild(text); 
		}
	}

	refresh() {
		this.refreshEntropy();
		this.draw();
	}
}


