class CommentsWidget extends _BaseWidget__WEBPACK_IMPORTED_MODULE_0__.BaseWidget {
	constructor() {
		super('Comments');
	}

	init() {
		_core_R2Wrapper__WEBPACK_IMPORTED_MODULE_5__.r2Wrapper.registerListener(_core_R2Wrapper__WEBPACK_IMPORTED_MODULE_5__.R2Actions.SEEK, () => {
			if (this.displayed) {
				this.draw();
			}
		});
	}

	draw() {
		this.node.innerHTML = '';
		this.node.appendChild(this.getPanel());
	}

	getPanel() {
		var c = document.createElement('div');

		var header = document.createElement('div');
		header.style.position = 'fixed';
		header.style.margin = '0.5em';
		c.appendChild(header);

		header.appendChild(_helpers_Inputs__WEBPACK_IMPORTED_MODULE_3__.Inputs.button('Notes', () => _core_UIContext__WEBPACK_IMPORTED_MODULE_1__.uiContext.navigateTo(_Widgets__WEBPACK_IMPORTED_MODULE_2__.Widgets.NOTES)));

		var content = document.createElement('div');
		content.style.paddingTop = '70px';
		c.appendChild(content);

		r2.cmd('CC', (d) => {
			var table = new _helpers_Table__WEBPACK_IMPORTED_MODULE_4__.Table(
				['+Offset', '~Comment'],
				[true, false],
				'commentsTable',
				(row, newVal) => {
					var offset = row[0];

					// remove
					r2.cmd('CC- @ ' + offset);

					// add new
					r2.cmd('CCu base64:' + window.btoa(newVal) + ' @ ' + offset);

					this.draw();
				},
				_Widgets__WEBPACK_IMPORTED_MODULE_2__.Widgets.HEXDUMP);

			var lines = d.split(/\n/); //clickable offsets (d).split (/\n/);
			for (var i in lines) {
				var line = lines[i].split(/ (.+)?/);
				if (line.length >= 2) {
					table.addRow([line[0], line[1]]);
				}
			}
			table.insertInto(content);
		});

		return c;
	}
}


