class WidgetFactory {

	constructor() {
		this.widgets = {};
	}

	get(widget) {
		if (!this.contains(widget)) {
			this.instanciate_(widget);
		}

		return this.widgets[widget];
	}

	contains(widget) {
		return typeof this.widgets[widget] !== 'undefined';
	}

	instanciate_(widget) {
		switch (widget) {
		case _Widgets__WEBPACK_IMPORTED_MODULE_0__.Widgets.OVERVIEW:
			this.widgets[widget] = new _OverviewWidget__WEBPACK_IMPORTED_MODULE_1__.OverviewWidget();
			break;
		case _Widgets__WEBPACK_IMPORTED_MODULE_0__.Widgets.DISASSEMBLY:
			this.widgets[widget] = new _DisassemblyWidget__WEBPACK_IMPORTED_MODULE_12__.DisassemblyWidget();
			break;
		case _Widgets__WEBPACK_IMPORTED_MODULE_0__.Widgets.DISASSEMBLY_GRAPH:
