		case _Widgets__WEBPACK_IMPORTED_MODULE_0__.Widgets.DISASSEMBLY_FUNCTIONS_FULL:
			this.widgets[widget] = new _DisassemblyFunctionsFullWidget__WEBPACK_IMPORTED_MODULE_16__.DisassemblyFunctionsFullWidget();
