		case _Widgets__WEBPACK_IMPORTED_MODULE_0__.Widgets.DISASSEMBLY_BLOCKS:
			this.widgets[widget] = new _DisassemblyBlocksWidget__WEBPACK_IMPORTED_MODULE_17__.DisassemblyBlocksWidget();
			break;
		case _Widgets__WEBPACK_IMPORTED_MODULE_0__.Widgets.DISASSEMBLY_DECOMPILE:
